package servlet;import model.User;import service.UserService;import javax.servlet.annotation.WebServlet;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;@WebServlet("/admin/delete/")public class DeleteUserServlet extends HttpServlet {    @Override    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws IOException {            Long id = Long.parseLong(request.getParameter("id"));            User user = UserService.getInstance().getUserById(id);            UserService.getInstance().deleteUser(user);            response.sendRedirect("/admin/users");    }    @Override    protected void doGet(HttpServletRequest request, HttpServletResponse response) {    }}