package servlet;import model.User;import service.UserService;import javax.servlet.annotation.WebServlet;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;import java.sql.SQLException;@WebServlet("/delete/*")public class DeleteUserServlet extends HttpServlet {    @Override    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws IOException {        Long id = Long.parseLong(request.getParameter("id"));        User user = null;        try {            user = UserService.getInstance().getUserById(id);        } catch (SQLException throwables) {            throwables.printStackTrace();        }        try {            UserService.getInstance().deleteUser(user);        } catch (SQLException throwables) {            throwables.printStackTrace();        }        response.sendRedirect("/reg");    }    @Override    protected void doGet(HttpServletRequest request, HttpServletResponse response){    }}